mapper.select:
select 

from agree
where user_id = #{userId,jdbcType=INTEGER}
  and say_id = #{sayId,jdbcType=INTEGER}

mapper.delete:
delete from agree
where user_id = #{userId,jdbcType=INTEGER}
  and say_id = #{sayId,jdbcType=INTEGER}

mapper.update:
update agree


    create_time = #{createTime,jdbcType=TIMESTAMP},


    modify_time = #{modifyTime,jdbcType=TIMESTAMP},


where user_id = #{userId,jdbcType=INTEGER}
  and say_id = #{sayId,jdbcType=INTEGER}

update agree
set create_time = #{createTime,jdbcType=TIMESTAMP},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=INTEGER}
  and say_id = #{sayId,jdbcType=INTEGER}
